var conf = msg.payload;
flow.set('conf', conf);
msg = {
    'method' : 'POST',
    'url' : conf.auth_base_url + "json/realms/root/realms/" + conf.realm + '/authenticate',
    'headers' : {
        'Accept-Api-Version': conf.api_version,
          'X-Username': 'anonymous',
          'X-Password': 'anonymous',
          'Content-Type': 'application/json',
          'Accept': 'application/json'
    }
}
return msg;

var authId = msg.payload.authId;
flow.set('authId', authId);
var conf = flow.get('conf');
msg = {
    'method' : 'POST',
    'url' : conf.auth_base_url + "json/realms/root/realms/" + conf.realm + '/authenticate',
    'headers' : {
        'Accept-Api-Version': conf.api_version,
          'X-Username': 'anonymous',
          'X-Password': 'anonymous',
          'Content-Type': 'application/json',
          'Accept': 'application/json'
    }
}
msg.payload = {
  'authId': authId,
  'template': '',
  'stage': 'LDAP1',
  'header': 'Sign in',
  'callbacks': [
    {
      'type': 'NameCallback',
      'output': [{'name': 'prompt', 'value': 'User Name:'}],
      'input': [{'name': 'IDToken1', 'value': conf.username}]
    },
    {
      'type': 'PasswordCallback',
      'output': [{'name': 'prompt', 'value': 'Password:'}],
      'input': [{'name': 'IDToken2', 'value': conf.password}]
    }
  ]
}

return msg;

var retry = flow.get('retry') || 0;
if (msg.statusCode == 401 && retry < 10) {
    retry++;
    flow.set('retry', retry);
    return [null, {payload: {authId: flow.get('authId')}}];
}
flow.set('retry', 0);
var conf = flow.get('conf');
var authCookie = msg.payload.tokenId;
var realm = msg.payload.realm; 
flow.set('realm', realm);
msg = {
    method: 'GET',
    followRedirects: false,
    rejectUnauthorized: false,
    url: conf.auth_base_url + "oauth2" + realm + "/authorize?client_id=" + conf.client_id + "&redirect_uri=" + conf.redirect_uri + "&response_type=code&scope=" + conf.scope + "&nonce=sdfdsfez&state=af0ifjsldkj",
    headers : {
        'Cookie': 'i18next=en-UK; amlbcookie=05; kauthSession="' + authCookie + '"'
    }
}

return [msg, null];

var resp = msg.headers.location;
var code = resp.split('=')[1].split('&')[0];
var conf = flow.get('conf');
var realm = flow.get('realm');
msg = {
    'method' : 'POST',
    'url' : conf.auth_base_url + 'oauth2' + realm + '/access_token?code=' + code + '&client_id=' + conf.client_id + '&client_secret=' + conf.client_secret + '&redirect_uri=' + conf.redirect_uri + '&grant_type=authorization_code',
    'headers' : {
      'Content-Type': 'application/x-www-form-urlencoded'
    }
}
return msg;

var bearerToken = msg.payload.access_token;
flow.set('bearerToken', bearerToken);
var conf = flow.get('conf');
msg = {
    'method': 'GET',
    'url': conf.user_adapter_base_url + 'v1/users/current',
    'headers': {
        'Authorization' : 'Bearer ' +  bearerToken
    }
}
return msg;

var userId = msg.payload.userId;
flow.set('userId', userId);
var conf = flow.get('conf');
var bearerToken = flow.get('bearerToken');
msg = {
    'method': 'GET',
    'url': conf.user_base_url + 'v5/users/' + userId + '/cars',
    'headers': {
        'Authorization' : 'Bearer ' +  bearerToken
    }
}
return msg;

var cars = msg.payload.data;
var session = {
    'bearerToken': flow.get('bearerToken'),
    'userId' : flow.get('userId')
}
if (cars.length == 0 ) {
    node.warn('No cars found!');
    session.vehicle = null;
}
else {
    session.vehicle = cars[0];
    node.warn('NissanConnect found car. Login complete');
}
msg.payload = session;
return msg;
